mapper.insert:
INSERT INTO t_common
(main_code, name, order_seq, ref_data1, ref_data2, ref_data3,
ref_data4, hide, insert_code, insert_date, update_code, update_date)
values( #{main_code}, #{name}, #{order_seq}, #{ref_data1},
#{ref_data2}, #{ref_data3}, #{ref_data4}, #{hide}, #{update_code},
#{update_date}, #{update_code}, localtime())
ON DUPLICATE KEY UPDATE
main_code = #{main_code}
, order_seq = #{order_seq}
, ref_data1 = #{ref_data1}
, ref_data2 = #{ref_data2}
, ref_data3 = #{ref_data3}
, ref_data4 = #{ref_data4}
, hide = #{hide}
, update_code = #{update_code}
, update_date = localtime();

mapper.delete:
delete from t_common

	
		and code like #{code}
	
	
		and main_code = #{main_code}

mapper.update:
		update T_common
		set main_code = #{main_code}
		, order_seq = #{order_seq}
		, ref_data1 = #{ref_data1}
		, ref_data2 = #{ref_data2}
		, ref_data3 = #{ref_data3}
		, ref_data4 = #{ref_data4}
		, hide = #{hide}
		, update_code = #{update_code}
		, update_date = localtime();

		where code = #{code}
